CREATE TABLE advert_translation (id BIGINT, active TINYINT(1), lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE advert (id BIGINT AUTO_INCREMENT, name TEXT, filename TEXT NOT NULL, url TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE catalogue (cat_id BIGINT AUTO_INCREMENT, name VARCHAR(100), source_lang VARCHAR(100), target_lang VARCHAR(100), date_created BIGINT, date_modified BIGINT, author VARCHAR(255), PRIMARY KEY(cat_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE city_translation (id BIGINT, name TEXT NOT NULL, lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE city (id BIGINT AUTO_INCREMENT, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE listing_translation (id BIGINT, description TEXT, month_deal TINYINT(1) DEFAULT '0', lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE listing (id BIGINT AUTO_INCREMENT, price FLOAT(18, 2) NOT NULL, city_id BIGINT NOT NULL, location_id BIGINT NOT NULL, street TEXT NOT NULL, is_house TINYINT(1) DEFAULT '0', area BIGINT NOT NULL, land_area BIGINT NOT NULL, rooms BIGINT NOT NULL, floors BIGINT NOT NULL, floor BIGINT, is_renovated TINYINT(1) DEFAULT '0', is_furnished TINYINT(1) DEFAULT '0', for_rent TINYINT(1) DEFAULT '0', is_active TINYINT(1) DEFAULT '1', has_videos TINYINT(1) DEFAULT '0', num_views BIGINT DEFAULT 0, google_map_location TEXT, main_photo TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX city_id_idx (city_id), INDEX location_id_idx (location_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE location_translation (id BIGINT, name TEXT NOT NULL, lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE location (id BIGINT AUTO_INCREMENT, city_id BIGINT NOT NULL, INDEX city_id_idx (city_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE messages (id BIGINT AUTO_INCREMENT, reply_to BIGINT, listing_id BIGINT, name VARCHAR(255) NOT NULL, company VARCHAR(255), message TEXT NOT NULL, contact TEXT NOT NULL, INDEX reply_to_idx (reply_to), INDEX listing_id_idx (listing_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE news_translation (id BIGINT, content TEXT NOT NULL, lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE news (id BIGINT AUTO_INCREMENT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE property_photos (id BIGINT AUTO_INCREMENT, listing_id BIGINT NOT NULL, filename TEXT NOT NULL, INDEX listing_id_idx (listing_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE slider_photos (id BIGINT AUTO_INCREMENT, filename TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE text_page_translation (id BIGINT, content TEXT NOT NULL, lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE text_page (id BIGINT AUTO_INCREMENT, page_name VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE trans_unit (msg_id BIGINT AUTO_INCREMENT, cat_id BIGINT, id VARCHAR(255), source LONGTEXT, target LONGTEXT, comments LONGTEXT, date_added BIGINT, date_modified BIGINT, author VARCHAR(255), translated TINYINT(1) DEFAULT '0', INDEX cat_id_idx (cat_id), PRIMARY KEY(msg_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
ALTER TABLE advert_translation ADD CONSTRAINT advert_translation_id_advert_id FOREIGN KEY (id) REFERENCES advert(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE city_translation ADD CONSTRAINT city_translation_id_city_id FOREIGN KEY (id) REFERENCES city(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE listing_translation ADD CONSTRAINT listing_translation_id_listing_id FOREIGN KEY (id) REFERENCES listing(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE listing ADD CONSTRAINT listing_location_id_location_id FOREIGN KEY (location_id) REFERENCES location(id) ON DELETE RESTRICT;
ALTER TABLE listing ADD CONSTRAINT listing_city_id_city_id FOREIGN KEY (city_id) REFERENCES city(id) ON DELETE RESTRICT;
ALTER TABLE location_translation ADD CONSTRAINT location_translation_id_location_id FOREIGN KEY (id) REFERENCES location(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE location ADD CONSTRAINT location_city_id_city_id FOREIGN KEY (city_id) REFERENCES city(id) ON DELETE CASCADE;
ALTER TABLE messages ADD CONSTRAINT messages_reply_to_messages_id FOREIGN KEY (reply_to) REFERENCES messages(id) ON DELETE CASCADE;
ALTER TABLE messages ADD CONSTRAINT messages_listing_id_listing_id FOREIGN KEY (listing_id) REFERENCES listing(id) ON DELETE CASCADE;
ALTER TABLE news_translation ADD CONSTRAINT news_translation_id_news_id FOREIGN KEY (id) REFERENCES news(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE property_photos ADD CONSTRAINT property_photos_listing_id_listing_id FOREIGN KEY (listing_id) REFERENCES listing(id) ON DELETE CASCADE;
ALTER TABLE text_page_translation ADD CONSTRAINT text_page_translation_id_text_page_id FOREIGN KEY (id) REFERENCES text_page(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE trans_unit ADD CONSTRAINT trans_unit_cat_id_catalogue_cat_id FOREIGN KEY (cat_id) REFERENCES catalogue(cat_id);
ALTER TABLE sf_guard_forgot_password ADD CONSTRAINT sf_guard_forgot_password_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_remember_key ADD CONSTRAINT sf_guard_remember_key_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
